$(function(){$("#searchForm").on("submit",function(a){a.preventDefault();var b=$("#mainSearchInputField");$.ajax({url:"/search",method:"POST",contentType:"application/json",data:JSON.stringify({restaurantQuery:b.val()}),success:function(a){if($(".hero-browser-inner").empty(),a[0].menu_item_name)for(let b=0;10>b;b++)$(".hero-browser-inner").append(`
              <p>
                <strong>Menu Item:</strong> ${a[b].menu_item_name} 
                <br /> 
                <strong>Calories:</strong> ${a[b].calories_kcal}
                <br />
                <strong>Total Fat:</strong> ${a[b].total_fat_grams}g
                <br />
                <strong>Total Carbs:</strong> ${a[b].carbohydrates_grams}g
                <br />
                <strong>Protein:</strong> ${a[b].protein_grams}g
                <br />
                <strong>Saturated Fat:</strong> ${a[b].saturated_fat_grams}g
                <br />
                <strong>Trans Fat:</strong> ${a[b].trans_fat_grams}g
                <br>
                <strong>Cholesterol:</strong> ${a[b].cholesterol_miligrams}mg
                <br />
                <strong>Sodium:</strong> ${a[b].sodium_miligrams}mg
                <br />
                <strong>Dietary Fiber:</strong> ${a[b].dietary_fiber_grams}g
                <br />
                <strong>Sugar:</strong> ${a[b].sugar_grams}g
              </p>`);else $(".hero-browser-inner").append(`Nutritional data coming soon!`);b.val("")}})})});